mapper.insert:
INSERT INTO repayment_record (
    customer_id, loan_id, repayment_amount, principal, interest,
    repayment_type, repayment_time, operator_id, remark, status, create_time
) VALUES (
             #{customerId}, #{loanId}, #{repaymentAmount}, #{principal}, #{interest},
             #{repaymentType}, #{repaymentTime}, #{operatorId}, #{remark}, #{status}, #{createTime}
         )

mapper.select:
SELECT  FROM repayment_record
WHERE loan_id = #{loanId}
ORDER BY repayment_time DESC

SELECT  FROM repayment_record
WHERE customer_id = #{customerId}
ORDER BY repayment_time DESC

SELECT

FROM repayment_record

    AND customer_id = #{customerId}
    AND loan_id = #{loanId}
    AND repayment_type = #{repaymentType}
    AND status = #{status}

ORDER BY repayment_time DESC
LIMIT #{start}, #{limit}
