mapper.insert:
INSERT INTO loan_grant (
    loan_id, customer_id, grant_amount, grant_time, operator_id,
    remark, status, create_time
) VALUES (
             #{loanId}, #{customerId}, #{grantAmount}, #{grantTime}, #{operatorId},
             #{remark}, #{status}, #{createTime}
         )

mapper.select:
SELECT  FROM loan_grant
WHERE loan_id = #{loanId}
ORDER BY grant_time DESC

SELECT  FROM loan_grant
WHERE customer_id = #{customerId}
ORDER BY grant_time DESC
